
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';

class FileUtils{
  static const  base64 =  "data:image/gif;base64,";

  static String? listToString(List<String> list) {
    if (list == null) {
      return null;
    }
    String? result;
    list.forEach((string) =>
    {if (result == null) result = string else result = '$result-$string'});
    return result.toString();
  }

  static String subStringName(String input){
    int startIndex = input.lastIndexOf('/') + 1;
    int endIndex = input.lastIndexOf('.');
    String result = input.substring(startIndex, endIndex);
    return result;
  }

  static String setTitle(String content) {
    int newlineIndex = content.indexOf('\n');
    if (newlineIndex != -1) {
      // 如果存在换行符，则截取第0个字符到换行符前面的字符并返回
      return content.substring(0, newlineIndex);
    } else {
      // 如果没有换行符，则直接返回当前字符串
      return content;
    }
  }

  //标题移除特殊字符
  static String removeSpecialCharacters(String input) {
    // 定义正则表达式，匹配除了字母、数字、空格以外的所有字符
    RegExp regExp = RegExp(r"[^a-zA-Z0-9\s]");

    // 使用replaceAll方法替换匹配的字符为空字符串
    return input.replaceAll(regExp, "");
  }

  //粘贴到粘贴板
  static void copyToClipboard(String text,BuildContext context) {
    Clipboard.setData(ClipboardData(text: text));
    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(
        content: Text('笔记📒已复制: $text'),
      ),
    );
  }

}

